import React from 'react';
import { motion } from 'framer-motion';
import { Course } from '../types';
import CourseCard from './CourseCard';
import { GraduationCap, Calendar } from 'lucide-react';

interface YearSectionProps {
  year: number;
  fallCourses: Course[];
  winterCourses: Course[];
  onCourseClick: (course: Course) => void;
  selectedCourse: Course | null;
}

const YearSection: React.FC<YearSectionProps> = ({
  year,
  fallCourses,
  winterCourses,
  onCourseClick,
  selectedCourse,
}) => {
  const totalUnits = fallCourses.reduce((sum, course) => sum + course.units, 0) +
                    winterCourses.reduce((sum, course) => sum + course.units, 0);

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
      className="mb-8"
    >
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center space-x-3">
          <div className="flex items-center justify-center w-12 h-12 bg-primary-100 rounded-full">
            <GraduationCap className="w-6 h-6 text-primary-600" />
          </div>
          <div>
            <h2 className="text-2xl font-bold text-gray-900">Year {year}</h2>
            <p className="text-gray-600">{totalUnits} units total</p>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Fall Semester */}
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5, delay: 0.1 }}
          className="space-y-4"
        >
          <div className="flex items-center space-x-2 mb-4">
            <Calendar className="w-5 h-5 text-orange-500" />
            <h3 className="text-xl font-semibold text-gray-900">Fall Semester</h3>
            <span className="text-sm text-gray-500">
              ({fallCourses.reduce((sum, course) => sum + course.units, 0)} units)
            </span>
          </div>
          
          <div className="grid gap-4">
            {fallCourses.map((course) => (
              <CourseCard
                key={course.id}
                course={course}
                onClick={onCourseClick}
                isSelected={selectedCourse?.id === course.id}
              />
            ))}
          </div>
        </motion.div>

        {/* Winter Semester */}
        <motion.div
          initial={{ opacity: 0, x: 20 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="space-y-4"
        >
          <div className="flex items-center space-x-2 mb-4">
            <Calendar className="w-5 h-5 text-blue-500" />
            <h3 className="text-xl font-semibold text-gray-900">Winter Semester</h3>
            <span className="text-sm text-gray-500">
              ({winterCourses.reduce((sum, course) => sum + course.units, 0)} units)
            </span>
          </div>
          
          <div className="grid gap-4">
            {winterCourses.map((course) => (
              <CourseCard
                key={course.id}
                course={course}
                onClick={onCourseClick}
                isSelected={selectedCourse?.id === course.id}
              />
            ))}
          </div>
        </motion.div>
      </div>
    </motion.div>
  );
};

export default YearSection;
