import React from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Course } from '../types';
import { X, BookOpen, Clock, ArrowRight, CheckCircle, AlertCircle } from 'lucide-react';

interface CourseModalProps {
  course: Course | null;
  onClose: () => void;
  allCourses: Course[];
}

const CourseModal: React.FC<CourseModalProps> = ({ course, onClose, allCourses }) => {
  if (!course) return null;

  const getPrerequisiteCourses = (prereqCodes: string[]) => {
    return prereqCodes.map(code => allCourses.find(c => c.code === code)).filter(Boolean) as Course[];
  };

  const getCorequisiteCourses = (coreqCodes: string[]) => {
    return coreqCodes.map(code => allCourses.find(c => c.code === code)).filter(Boolean) as Course[];
  };

  const prerequisiteCourses = getPrerequisiteCourses(course.prerequisites);
  const corequisiteCourses = getCorequisiteCourses(course.corequisites);

  const getCategoryColor = (category: string) => {
    switch (category) {
      case 'Core':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'Technical':
        return 'bg-purple-100 text-purple-800 border-purple-200';
      case 'Elective':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'Complementary':
        return 'bg-orange-100 text-orange-800 border-orange-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getTagColor = (tag: string) => {
    switch (tag) {
      case 'T':
        return 'bg-teal-100 text-teal-800';
      case 'E':
        return 'bg-emerald-100 text-emerald-800';
      case 'M':
        return 'bg-indigo-100 text-indigo-800';
      case 'P':
        return 'bg-rose-100 text-rose-800';
      case 'S':
        return 'bg-amber-100 text-amber-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        className="modal-overlay"
        onClick={onClose}
      >
        <motion.div
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          exit={{ scale: 0.9, opacity: 0 }}
          className="modal-content"
          onClick={(e) => e.stopPropagation()}
        >
          <div className="p-6">
            <div className="flex items-center justify-between mb-6">
              <div>
                <h2 className="text-2xl font-bold text-gray-900">{course.code}</h2>
                <p className="text-lg text-gray-600 mt-1">{course.name}</p>
              </div>
              <button
                onClick={onClose}
                className="p-2 rounded-full hover:bg-gray-100 transition-colors"
              >
                <X className="w-6 h-6 text-gray-500" />
              </button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="space-y-4">
                <div className="flex items-center space-x-2">
                  <Clock className="w-5 h-5 text-gray-400" />
                  <span className="text-gray-700">{course.units} units</span>
                </div>

                <div className="flex items-center space-x-2">
                  <BookOpen className="w-5 h-5 text-gray-400" />
                  <span className="text-gray-700">{course.components.join(', ')}</span>
                </div>

                <div className="flex items-center space-x-2">
                  <span className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium border ${getCategoryColor(course.category)}`}>
                    {course.category}
                  </span>
                </div>

                {course.tags && course.tags.length > 0 && (
                  <div className="flex items-center space-x-2">
                    <span className="text-gray-700">Tags:</span>
                    <div className="flex space-x-1">
                      {course.tags.map((tag, index) => (
                        <span
                          key={index}
                          className={`inline-flex items-center px-2 py-1 rounded text-sm font-medium ${getTagColor(tag)}`}
                        >
                          {tag}
                        </span>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              <div className="space-y-4">
                <div>
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">Description</h3>
                  <p className="text-gray-700 leading-relaxed">{course.description}</p>
                </div>

                {course.prerequisites.length > 0 && (
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900 mb-2 flex items-center">
                      <CheckCircle className="w-5 h-5 text-green-500 mr-2" />
                      Prerequisites
                    </h3>
                    <div className="space-y-2">
                      {prerequisiteCourses.map((prereq) => (
                        <div key={prereq.id} className="flex items-center space-x-2 p-2 bg-green-50 rounded-lg">
                          <ArrowRight className="w-4 h-4 text-green-500" />
                          <span className="font-medium text-green-800">{prereq.code}</span>
                          <span className="text-green-600">{prereq.name}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}

                {course.corequisites.length > 0 && (
                  <div>
                    <h3 className="text-lg font-semibold text-gray-900 mb-2 flex items-center">
                      <AlertCircle className="w-5 h-5 text-blue-500 mr-2" />
                      Corequisites
                    </h3>
                    <div className="space-y-2">
                      {corequisiteCourses.map((coreq) => (
                        <div key={coreq.id} className="flex items-center space-x-2 p-2 bg-blue-50 rounded-lg">
                          <ArrowRight className="w-4 h-4 text-blue-500" />
                          <span className="font-medium text-blue-800">{coreq.code}</span>
                          <span className="text-blue-600">{coreq.name}</span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            </div>

            <div className="mt-6 pt-6 border-t border-gray-200">
              <div className="flex items-center justify-between text-sm text-gray-500">
                <span>Year {course.year} â€¢ {course.semester} Semester</span>
                <span>Course ID: {course.id}</span>
              </div>
            </div>
          </div>
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
};

export default CourseModal;
