import React, { useRef, useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Course } from '../types';
import CourseCard from './CourseCard';
import { ArrowRight, ArrowDown, ArrowUpRight, Minus } from 'lucide-react';

interface EnhancedFlowchartProps {
  courses: Course[];
  onCourseClick: (course: Course) => void;
  selectedCourse: Course | null;
}

const EnhancedFlowchart: React.FC<EnhancedFlowchartProps> = ({
  courses,
  onCourseClick,
  selectedCourse,
}) => {
  const [coursePositions, setCoursePositions] = useState<{ [key: string]: { x: number; y: number } }>({});
  const containerRef = useRef<HTMLDivElement>(null);

  // Group courses by year and semester
  const getCoursesByYear = () => {
    const years: { [key: number]: { fall: Course[], winter: Course[] } } = {};
    
    courses.forEach(course => {
      if (!years[course.year]) {
        years[course.year] = { fall: [], winter: [] };
      }
      
      if (course.semester === 'Fall') {
        years[course.year].fall.push(course);
      } else {
        years[course.year].winter.push(course);
      }
    });

    return years;
  };

  const yearsData = getCoursesByYear();

  // Calculate course positions after render
  useEffect(() => {
    const positions: { [key: string]: { x: number; y: number } } = {};
    
    Object.entries(yearsData).forEach(([year, { fall, winter }]) => {
      const yearIndex = parseInt(year);
      
      // Fall semester courses
      fall.forEach((course, index) => {
        const element = document.getElementById(`course-${course.id}`);
        if (element) {
          const rect = element.getBoundingClientRect();
          const containerRect = containerRef.current?.getBoundingClientRect();
          if (containerRect) {
            positions[course.id] = {
              x: rect.left - containerRect.left + rect.width / 2,
              y: rect.top - containerRect.top + rect.height / 2
            };
          }
        }
      });

      // Winter semester courses
      winter.forEach((course, index) => {
        const element = document.getElementById(`course-${course.id}`);
        if (element) {
          const rect = element.getBoundingClientRect();
          const containerRect = containerRef.current?.getBoundingClientRect();
          if (containerRect) {
            positions[course.id] = {
              x: rect.left - containerRect.left + rect.width / 2,
              y: rect.top - containerRect.top + rect.height / 2
            };
          }
        }
      });
    });

    setCoursePositions(positions);
  }, [courses, yearsData]);

  const renderPrerequisiteConnections = () => {
    const connections: JSX.Element[] = [];
    
    courses.forEach(course => {
      course.prerequisites.forEach(prereqCode => {
        const prereqCourse = courses.find(c => c.code === prereqCode);
        if (prereqCourse && coursePositions[prereqCourse.id] && coursePositions[course.id]) {
          const from = coursePositions[prereqCourse.id];
          const to = coursePositions[course.id];
          
          // Create curved path
          const midX = (from.x + to.x) / 2;
          const midY = from.y - 30;
          
          const pathData = `M ${from.x} ${from.y} Q ${midX} ${midY} ${to.x} ${to.y}`;
          
          connections.push(
            <svg
              key={`${prereqCourse.id}-${course.id}`}
              className="absolute inset-0 pointer-events-none"
              style={{ zIndex: 1 }}
            >
              <defs>
                <marker
                  id={`arrow-${prereqCourse.id}-${course.id}`}
                  markerWidth="10"
                  markerHeight="7"
                  refX="9"
                  refY="3.5"
                  orient="auto"
                >
                  <polygon
                    points="0 0, 10 3.5, 0 7"
                    fill="#10b981"
                  />
                </marker>
              </defs>
              
              <path
                d={pathData}
                stroke="#10b981"
                strokeWidth="2"
                fill="none"
                markerEnd={`url(#arrow-${prereqCourse.id}-${course.id})`}
                strokeDasharray="5,5"
                opacity="0.7"
              />
            </svg>
          );
        }
      });
    });
    
    return connections;
  };

  const renderSemesterConnections = (semesterCourses: Course[]) => {
    return semesterCourses.map((course, index) => {
      if (index < semesterCourses.length - 1) {
        return (
          <div
            key={`connector-${course.id}`}
            className="absolute top-1/2 -right-6 transform -translate-y-1/2 flex items-center"
          >
            <div className="w-8 h-0.5 bg-gray-300"></div>
            <ArrowRight className="w-4 h-4 text-gray-400 ml-1" />
          </div>
        );
      }
      return null;
    });
  };

  return (
    <div ref={containerRef} className="relative">
      {/* Prerequisite connections */}
      {renderPrerequisiteConnections()}
      
      <div className="space-y-16">
        {Object.entries(yearsData)
          .sort(([a], [b]) => parseInt(a) - parseInt(b))
          .map(([year, { fall, winter }]) => (
            <motion.div
              key={year}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              className="relative"
            >
              {/* Year Header */}
              <div className="text-center mb-12">
                <div className="inline-flex items-center space-x-4 bg-white px-8 py-4 rounded-full shadow-lg border-2 border-gray-200">
                  <div className="w-8 h-8 bg-primary-500 rounded-full flex items-center justify-center">
                    <span className="text-white font-bold text-lg">{year}</span>
                  </div>
                  <h2 className="text-2xl font-bold text-gray-900">Year {year}</h2>
                </div>
              </div>

              <div className="space-y-20">
                {/* Fall Semester */}
                <div className="relative">
                  <div className="text-center mb-8">
                    <h3 className="text-xl font-semibold text-orange-600 bg-orange-50 px-6 py-3 rounded-full inline-block border-2 border-orange-200">
                      🍂 Fall Semester
                    </h3>
                  </div>
                  
                  <div className="flex items-center justify-center space-x-12 flex-wrap">
                    {fall.map((course, index) => (
                      <div key={course.id} className="relative">
                        <div id={`course-${course.id}`} className="w-72">
                          <CourseCard
                            course={course}
                            onClick={onCourseClick}
                            isSelected={selectedCourse?.id === course.id}
                          />
                        </div>
                        
                        {/* Semester flow connector */}
                        {renderSemesterConnections(fall)[index]}
                      </div>
                    ))}
                  </div>
                </div>

                {/* Semester transition */}
                <div className="flex justify-center">
                  <div className="flex flex-col items-center space-y-3">
                    <div className="w-1 h-16 bg-gradient-to-b from-orange-400 to-blue-400 rounded-full"></div>
                    <div className="flex items-center space-x-2 bg-white px-4 py-2 rounded-full shadow-sm border">
                      <ArrowDown className="w-5 h-5 text-gray-500" />
                      <span className="text-sm font-medium text-gray-600">Next Semester</span>
                    </div>
                  </div>
                </div>

                {/* Winter Semester */}
                <div className="relative">
                  <div className="text-center mb-8">
                    <h3 className="text-xl font-semibold text-blue-600 bg-blue-50 px-6 py-3 rounded-full inline-block border-2 border-blue-200">
                      ❄️ Winter Semester
                    </h3>
                  </div>
                  
                  <div className="flex items-center justify-center space-x-12 flex-wrap">
                    {winter.map((course, index) => (
                      <div key={course.id} className="relative">
                        <div id={`course-${course.id}`} className="w-72">
                          <CourseCard
                            course={course}
                            onClick={onCourseClick}
                            isSelected={selectedCourse?.id === course.id}
                          />
                        </div>
                        
                        {/* Semester flow connector */}
                        {renderSemesterConnections(winter)[index]}
                      </div>
                    ))}
                  </div>
                </div>

                {/* Year transition (except for last year) */}
                {parseInt(year) < Math.max(...Object.keys(yearsData).map(Number)) && (
                  <div className="flex justify-center mt-12">
                    <div className="flex flex-col items-center space-y-3">
                      <div className="w-1 h-20 bg-gradient-to-b from-blue-400 to-primary-400 rounded-full"></div>
                      <div className="flex items-center space-x-2 bg-white px-4 py-2 rounded-full shadow-sm border">
                        <ArrowDown className="w-5 h-5 text-gray-500" />
                        <span className="text-sm font-medium text-gray-600">Next Year</span>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </motion.div>
          ))}
      </div>
    </div>
  );
};

export default EnhancedFlowchart;
