import React, { useRef, useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Course } from '../types';
import CourseCard from './CourseCard';

interface PrerequisiteFlowchartProps {
  courses: Course[];
  onCourseClick: (course: Course) => void;
  selectedCourse: Course | null;
}

interface ArrowInfo {
  from: string;
  to: string;
  pathData: string;
  fromPos: { x: number; y: number; width: number; height: number };
  toPos: { x: number; y: number; width: number; height: number };
}

const PrerequisiteFlowchart: React.FC<PrerequisiteFlowchartProps> = ({
  courses,
  onCourseClick,
  selectedCourse,
}) => {
  const [coursePositions, setCoursePositions] = useState<{ [key: string]: { x: number; y: number; width: number; height: number } }>({});
  const [hoveredCourse, setHoveredCourse] = useState<string | null>(null);
  const containerRef = useRef<HTMLDivElement>(null);

  // Group courses by year and semester
  const getCoursesByYear = () => {
    const years: { [key: number]: { fall: Course[], winter: Course[] } } = {};
    
    courses.forEach(course => {
      if (!years[course.year]) {
        years[course.year] = { fall: [], winter: [] };
      }
      
      if (course.semester === 'Fall') {
        years[course.year].fall.push(course);
      } else {
        years[course.year].winter.push(course);
      }
    });

    return years;
  };

  const yearsData = getCoursesByYear();

  // Calculate course positions after render with delay
  useEffect(() => {
    const calculatePositions = () => {
      const positions: { [key: string]: { x: number; y: number; width: number; height: number } } = {};
      
      Object.entries(yearsData).forEach(([year, { fall, winter }]) => {
        // Fall semester courses
        fall.forEach((course) => {
          const element = document.getElementById(`course-${course.id}`);
          if (element) {
            const rect = element.getBoundingClientRect();
            const containerRect = containerRef.current?.getBoundingClientRect();
            if (containerRect) {
              positions[course.id] = {
                x: rect.left - containerRect.left + rect.width / 2,
                y: rect.top - containerRect.top + rect.height / 2,
                width: rect.width,
                height: rect.height
              };
            }
          }
        });

        // Winter semester courses
        winter.forEach((course) => {
          const element = document.getElementById(`course-${course.id}`);
          if (element) {
            const rect = element.getBoundingClientRect();
            const containerRect = containerRef.current?.getBoundingClientRect();
            if (containerRect) {
              positions[course.id] = {
                x: rect.left - containerRect.left + rect.width / 2,
                y: rect.top - containerRect.top + rect.height / 2,
                width: rect.width,
                height: rect.height
              };
            }
          }
        });
      });

      setCoursePositions(positions);
    };

    // Delay to ensure DOM is rendered
    setTimeout(calculatePositions, 100);
  }, [courses, yearsData]);

  const renderPrerequisiteArrows = () => {
    const arrows: JSX.Element[] = [];
    
    // Debug: Log prerequisite relationships
    console.log('Rendering prerequisite arrows...');
    console.log('Course positions:', coursePositions);
    
    courses.forEach(course => {
      course.prerequisites.forEach(prereqCode => {
        const prereqCourse = courses.find(c => c.code === prereqCode);
        console.log(`Course ${course.code} has prerequisite ${prereqCode}, found:`, prereqCourse);
        if (prereqCourse && coursePositions[prereqCourse.id] && coursePositions[course.id]) {
          const from = coursePositions[prereqCourse.id];
          const to = coursePositions[course.id];
          
          // Create curved path with better positioning
          const midX = (from.x + to.x) / 2;
          const midY = Math.min(from.y, to.y) - 80; // Curve upward more for better visibility
          
          const pathData = `M ${from.x} ${from.y} Q ${midX} ${midY} ${to.x} ${to.y}`;
          
          // Check if this arrow should be highlighted
          const isHighlighted = hoveredCourse === course.id || hoveredCourse === prereqCourse.id;
          const strokeColor = isHighlighted ? '#dc2626' : '#6b7280';
          const strokeWidth = isHighlighted ? '4' : '2';
          const opacity = isHighlighted ? '1' : '0.6';
          
          arrows.push(
            <svg
              key={`${prereqCourse.id}-${course.id}`}
              className="absolute inset-0 pointer-events-none"
              style={{ zIndex: 10 }}
            >
              <defs>
                <marker
                  id={`arrow-${prereqCourse.id}-${course.id}`}
                  markerWidth="15"
                  markerHeight="10"
                  refX="12"
                  refY="5"
                  orient="auto"
                >
                  <polygon
                    points="0 0, 15 5, 0 10"
                    fill={strokeColor}
                    stroke={strokeColor}
                    strokeWidth="1"
                  />
                </marker>
              </defs>
              
              <path
                d={pathData}
                stroke={strokeColor}
                strokeWidth={strokeWidth}
                fill="none"
                markerEnd={`url(#arrow-${prereqCourse.id}-${course.id})`}
                strokeDasharray="8,4"
                opacity={opacity}
                className="transition-all duration-200"
              />
              
              {/* Add a subtle glow effect */}
              <path
                d={pathData}
                stroke={strokeColor}
                strokeWidth="8"
                fill="none"
                opacity="0.1"
              />
            </svg>
          );
        }
      });
    });
    
    return arrows;
  };

  return (
    <div ref={containerRef} className="relative overflow-x-auto">
      {/* Debug info */}
      <div className="absolute top-2 left-2 bg-yellow-100 border border-yellow-300 rounded px-2 py-1 text-xs z-20">
        Arrows: {Object.keys(coursePositions).length} courses positioned
      </div>
      
      {/* Prerequisite arrows */}
      {renderPrerequisiteArrows()}
      
      <div className="min-w-max">
        <div className="flex space-x-16">
          {Object.entries(yearsData)
            .sort(([a], [b]) => parseInt(a) - parseInt(b))
            .map(([year, { fall, winter }]) => (
              <motion.div
                key={year}
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.5 }}
                className="flex-shrink-0"
              >
                {/* Year Header */}
                <div className="text-center mb-8">
                  <div className="inline-flex items-center space-x-4 bg-blue-100 px-6 py-3 rounded-lg border-2 border-blue-200">
                    <div className="w-6 h-6 bg-blue-500 rounded-full flex items-center justify-center">
                      <span className="text-white font-bold text-sm">{year}</span>
                    </div>
                    <h2 className="text-lg font-bold text-blue-900">Year {year}</h2>
                  </div>
                </div>

                {/* Semester Columns */}
                <div className="flex space-x-8">
                  {/* Fall Semester */}
                  <div className="flex flex-col space-y-4 min-w-[280px]">
                    <div className="text-center mb-4">
                      <h3 className="text-sm font-semibold text-red-600 bg-red-50 px-3 py-1 rounded border border-red-200">
                        Fall 202{parseInt(year) + 3}
                      </h3>
                    </div>
                    
                    {fall.map((course) => (
                      <div key={course.id} className="relative">
                        <div id={`course-${course.id}`} className="w-full">
                          <CourseCard
                            course={course}
                            onClick={onCourseClick}
                            isSelected={selectedCourse?.id === course.id}
                          />
                        </div>
                      </div>
                    ))}
                  </div>

                  {/* Winter Semester */}
                  <div className="flex flex-col space-y-4 min-w-[280px]">
                    <div className="text-center mb-4">
                      <h3 className="text-sm font-semibold text-blue-600 bg-blue-50 px-3 py-1 rounded border border-blue-200">
                        Winter 202{parseInt(year) + 4}
                      </h3>
                    </div>
                    
                    {winter.map((course) => (
                      <div key={course.id} className="relative">
                        <div id={`course-${course.id}`} className="w-full">
                          <CourseCard
                            course={course}
                            onClick={onCourseClick}
                            isSelected={selectedCourse?.id === course.id}
                          />
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </motion.div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default PrerequisiteFlowchart;
