import React, { useRef, useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Course, getPrerequisites, getCorequisites } from '../data/courses.ts';

interface FlowchartProps {
  selectedCourse: Course | null;
  onCourseClick: (course: Course) => void;
}

interface Connection {
  from: string;
  to: string;
  type: 'prerequisite' | 'corequisite';
}

const Flowchart: React.FC<FlowchartProps> = ({ selectedCourse, onCourseClick }) => {
  const svgRef = useRef<SVGSVGElement>(null);
  const [hoveredCourse, setHoveredCourse] = useState<Course | null>(null);
  const [connections, setConnections] = useState<Connection[]>([]);

  // Generate connections for the hovered course
  useEffect(() => {
    if (!hoveredCourse) {
      setConnections([]);
      return;
    }

    const newConnections: Connection[] = [];
    
    // Add prerequisite connections (courses that lead TO this course)
    const prerequisites = getPrerequisites(hoveredCourse.id);
    prerequisites.forEach(prereq => {
      newConnections.push({
        from: prereq.id,
        to: hoveredCourse.id,
        type: 'prerequisite'
      });
    });

    // Add corequisite connections
    const corequisites = getCorequisites(hoveredCourse.id);
    corequisites.forEach(coreq => {
      newConnections.push({
        from: coreq.id,
        to: hoveredCourse.id,
        type: 'corequisite'
      });
    });

    // Add connections TO courses that this course is a prerequisite for
    const allCourses = require('../data/courses.ts').courses;
    allCourses.forEach((course: Course) => {
      if (course.prerequisites.includes(hoveredCourse.id)) {
        newConnections.push({
          from: hoveredCourse.id,
          to: course.id,
          type: 'prerequisite'
        });
      }
      if (course.corequisites.includes(hoveredCourse.id)) {
        newConnections.push({
          from: hoveredCourse.id,
          to: course.id,
          type: 'corequisite'
        });
      }
    });

    setConnections(newConnections);
  }, [hoveredCourse]);

  const renderArrow = (from: string, to: string, type: 'prerequisite' | 'corequisite') => {
    const fromCourse = courses.find(c => c.id === from);
    const toCourse = courses.find(c => c.id === to);
    
    if (!fromCourse || !toCourse) return null;

    // Calculate positions based on year and semester
    const fromX = (fromCourse.year - 1) * 300 + (fromCourse.semester === 'Fall' ? 50 : 200);
    const fromY = 150 + (courses.filter(c => c.year === fromCourse.year && c.semester === fromCourse.semester).indexOf(fromCourse) * 80);
    
    const toX = (toCourse.year - 1) * 300 + (toCourse.semester === 'Fall' ? 50 : 200);
    const toY = 150 + (courses.filter(c => c.year === toCourse.year && c.semester === toCourse.semester).indexOf(toCourse) * 80);

    const dx = toX - fromX;
    const dy = toY - fromY;
    const angle = Math.atan2(dy, dx);
    
    // Adjust arrow start and end to avoid overlapping with course cards
    const offset = 60;
    const startX = fromX + Math.cos(angle) * offset;
    const startY = fromY + Math.sin(angle) * offset;
    const endX = toX - Math.cos(angle) * offset;
    const endY = toY - Math.sin(angle) * offset;

    const strokeColor = type === 'prerequisite' ? '#dc2626' : '#2563eb';
    const strokeWidth = type === 'prerequisite' ? 3 : 2;
    const dashArray = type === 'corequisite' ? '5,5' : 'none';

    return (
      <g key={`${from}-${to}`}>
        <line
          x1={startX}
          y1={startY}
          x2={endX}
          y2={endY}
          stroke={strokeColor}
          strokeWidth={strokeWidth}
          strokeDasharray={dashArray}
          markerEnd={`url(#arrowhead-${type})`}
        />
      </g>
    );
  };

  const renderCourseNode = (course: Course) => {
    const x = (course.year - 1) * 300 + (course.semester === 'Fall' ? 50 : 200);
    const y = 150 + (courses.filter(c => c.year === course.year && c.semester === course.semester).indexOf(course) * 80);
    
    const isHovered = hoveredCourse?.id === course.id;
    const isRelated = connections.some(conn => conn.from === course.id || conn.to === course.id);

    return (
      <motion.g
        key={course.id}
        initial={{ opacity: 0, scale: 0 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.3 }}
        onMouseEnter={() => setHoveredCourse(course)}
        onMouseLeave={() => setHoveredCourse(null)}
        className="cursor-pointer"
        onClick={() => onCourseClick(course)}
      >
        <rect
          x={x - 80}
          y={y - 30}
          width={160}
          height={60}
          rx={8}
          fill={isHovered ? '#fef3c7' : isRelated ? '#f0f9ff' : '#ffffff'}
          stroke={isHovered ? '#f59e0b' : isRelated ? '#0ea5e9' : '#000000'}
          strokeWidth={isHovered ? 3 : isRelated ? 2 : 2}
          className="transition-all duration-200"
        />
        <text
          x={x}
          y={y - 10}
          textAnchor="middle"
          className="text-sm font-bold"
          fill="#1f2937"
        >
          {course.code}
        </text>
        <text
          x={x}
          y={y + 5}
          textAnchor="middle"
          className="text-xs"
          fill="#6b7280"
        >
          {course.name.split(' ').slice(0, 2).join(' ')}
        </text>
        <text
          x={x}
          y={y + 20}
          textAnchor="middle"
          className="text-xs font-semibold"
          fill="#059669"
        >
          {course.units} units
        </text>
      </motion.g>
    );
  };

  const renderSemesterHeader = (year: number, semester: 'Fall' | 'Winter') => {
    const x = (year - 1) * 300 + (semester === 'Fall' ? 50 : 200);
    const y = 50;
    
    return (
      <g key={`${year}-${semester}`}>
        <rect
          x={x - 100}
          y={y - 20}
          width={200}
          height={40}
          rx={8}
          fill="#fecaca"
          stroke="#000000"
          strokeWidth={2}
        />
        <text
          x={x}
          y={y + 5}
          textAnchor="middle"
          className="text-sm font-bold"
          fill="#1f2937"
        >
          {semester} {2024 + year - 1}
        </text>
      </g>
    );
  };

  const courses = require('../data/courses.ts').courses;

  return (
    <div className="bg-white border-2 border-black shadow-brutal p-6">
      <h3 className="text-xl font-bold text-gray-800 mb-4 text-center">
        Course Timeline Flowchart
      </h3>
      
      <div className="relative w-full h-96 overflow-x-auto">
        <svg
          ref={svgRef}
          width="1200"
          height="400"
          className="absolute inset-0"
        >
          {/* Arrow markers */}
          <defs>
            <marker
              id="arrowhead-prerequisite"
              markerWidth="10"
              markerHeight="7"
              refX="9"
              refY="3.5"
              orient="auto"
            >
              <polygon
                points="0 0, 10 3.5, 0 7"
                fill="#dc2626"
              />
            </marker>
            <marker
              id="arrowhead-corequisite"
              markerWidth="10"
              markerHeight="7"
              refX="9"
              refY="3.5"
              orient="auto"
            >
              <polygon
                points="0 0, 10 3.5, 0 7"
                fill="#2563eb"
              />
            </marker>
          </defs>

          {/* Render semester headers */}
          {[1, 2, 3, 4].map(year => (
            <React.Fragment key={year}>
              {renderSemesterHeader(year, 'Fall')}
              {renderSemesterHeader(year, 'Winter')}
            </React.Fragment>
          ))}

          {/* Render connections when hovering */}
          {connections.map(connection => 
            renderArrow(connection.from, connection.to, connection.type)
          )}

          {/* Render course nodes */}
          {courses.map(course => renderCourseNode(course))}
        </svg>
      </div>

      <div className="mt-4 flex justify-center gap-6 text-sm">
        <div className="flex items-center gap-2">
          <div className="w-4 h-0.5 bg-red-600"></div>
          <span className="font-semibold text-red-600">Prerequisites</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-0.5 bg-blue-600 border-dashed border-blue-600"></div>
          <span className="font-semibold text-blue-600">Corequisites</span>
        </div>
      </div>

      <div className="mt-2 text-center text-xs text-gray-600">
        Hover over any course to see its relationships
      </div>
    </div>
  );
};

export default Flowchart;
