import React, { useRef, useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Course } from '../types';
import CourseCard from './CourseCard';

interface PrerequisiteFlowchartProps {
  courses: Course[];
  onCourseClick: (course: Course) => void;
  selectedCourse: Course | null;
}

const PrerequisiteFlowchart: React.FC<PrerequisiteFlowchartProps> = ({
  courses,
  onCourseClick,
  selectedCourse,
}) => {
  const [coursePositions, setCoursePositions] = useState<{ [key: string]: { x: number; y: number; width: number; height: number } }>({});
  const containerRef = useRef<HTMLDivElement>(null);

  // Group courses by year and semester
  const getCoursesByYear = () => {
    const years: { [key: number]: { fall: Course[], winter: Course[], summer?: Course[] } } = {};
    
    courses.forEach(course => {
      if (!years[course.year]) {
        years[course.year] = { fall: [], winter: [] };
      }
      
      if (course.semester === 'Fall') {
        years[course.year].fall.push(course);
      } else {
        years[course.year].winter.push(course);
      }
    });

    return years;
  };

  const yearsData = getCoursesByYear();

  // Calculate course positions after render
  useEffect(() => {
    const positions: { [key: string]: { x: number; y: number; width: number; height: number } } = {};
    
    Object.entries(yearsData).forEach(([year, { fall, winter }]) => {
      // Fall semester courses
      fall.forEach((course) => {
        const element = document.getElementById(`course-${course.id}`);
        if (element) {
          const rect = element.getBoundingClientRect();
          const containerRect = containerRef.current?.getBoundingClientRect();
          if (containerRect) {
            positions[course.id] = {
              x: rect.left - containerRect.left + rect.width / 2,
              y: rect.top - containerRect.top + rect.height / 2,
              width: rect.width,
              height: rect.height
            };
          }
        }
      });

      // Winter semester courses
      winter.forEach((course) => {
        const element = document.getElementById(`course-${course.id}`);
        if (element) {
          const rect = element.getBoundingClientRect();
          const containerRect = containerRef.current?.getBoundingClientRect();
          if (containerRect) {
            positions[course.id] = {
              x: rect.left - containerRect.left + rect.width / 2,
              y: rect.top - containerRect.top + rect.height / 2,
              width: rect.width,
              height: rect.height
            };
          }
        }
      });
    });

    setCoursePositions(positions);
  }, [courses, yearsData]);

  const renderPrerequisiteArrows = () => {
    const arrows: JSX.Element[] = [];
    
    courses.forEach(course => {
      course.prerequisites.forEach(prereqCode => {
        const prereqCourse = courses.find(c => c.code === prereqCode);
        if (prereqCourse && coursePositions[prereqCourse.id] && coursePositions[course.id]) {
          const from = coursePositions[prereqCourse.id];
          const to = coursePositions[course.id];
          
          // Create curved path
          const midX = (from.x + to.x) / 2;
          const midY = from.y - 40; // Curve upward
          
          const pathData = `M ${from.x} ${from.y} Q ${midX} ${midY} ${to.x} ${to.y}`;
          
          arrows.push(
            <svg
              key={`${prereqCourse.id}-${course.id}`}
              className="absolute inset-0 pointer-events-none"
              style={{ zIndex: 1 }}
            >
              <defs>
                <marker
                  id={`arrow-${prereqCourse.id}-${course.id}`}
                  markerWidth="10"
                  markerHeight="7"
                  refX="9"
                  refY="3.5"
                  orient="auto"
                >
                  <polygon
                    points="0 0, 10 3.5, 0 7"
                    fill="#10b981"
                  />
                </marker>
              </defs>
              
              <path
                d={pathData}
                stroke="#10b981"
                strokeWidth="3"
                fill="none"
                markerEnd={`url(#arrow-${prereqCourse.id}-${course.id})`}
                strokeDasharray="8,4"
                opacity="0.8"
              />
            </svg>
          );
        }
      });
    });
    
    return arrows;
  };

  return (
    <div ref={containerRef} className="relative overflow-x-auto">
      {/* Prerequisite arrows */}
      {renderPrerequisiteArrows()}
      
      <div className="min-w-max">
        {Object.entries(yearsData)
          .sort(([a], [b]) => parseInt(a) - parseInt(b))
          .map(([year, { fall, winter }]) => (
            <motion.div
              key={year}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              className="mb-16"
            >
              {/* Year Header */}
              <div className="text-center mb-8">
                <div className="inline-flex items-center space-x-4 bg-blue-100 px-8 py-4 rounded-lg border-2 border-blue-200">
                  <div className="w-8 h-8 bg-blue-500 rounded-full flex items-center justify-center">
                    <span className="text-white font-bold text-lg">{year}</span>
                  </div>
                  <h2 className="text-2xl font-bold text-blue-900">Year {year}</h2>
                </div>
              </div>

              {/* Semester Columns */}
              <div className="flex space-x-8 justify-center">
                {/* Fall Semester */}
                <div className="flex flex-col space-y-4 min-w-[300px]">
                  <div className="text-center mb-4">
                    <h3 className="text-lg font-semibold text-red-600 bg-red-50 px-4 py-2 rounded-lg border border-red-200">
                      Fall 202{parseInt(year) + 3}
                    </h3>
                  </div>
                  
                  {fall.map((course) => (
                    <div key={course.id} className="relative">
                      <div id={`course-${course.id}`} className="w-full">
                        <CourseCard
                          course={course}
                          onClick={onCourseClick}
                          isSelected={selectedCourse?.id === course.id}
                        />
                      </div>
                    </div>
                  ))}
                </div>

                {/* Winter Semester */}
                <div className="flex flex-col space-y-4 min-w-[300px]">
                  <div className="text-center mb-4">
                    <h3 className="text-lg font-semibold text-blue-600 bg-blue-50 px-4 py-2 rounded-lg border border-blue-200">
                      Winter 202{parseInt(year) + 4}
                    </h3>
                  </div>
                  
                  {winter.map((course) => (
                    <div key={course.id} className="relative">
                      <div id={`course-${course.id}`} className="w-full">
                        <CourseCard
                          course={course}
                          onClick={onCourseClick}
                          isSelected={selectedCourse?.id === course.id}
                        />
                      </div>
                    </div>
                  ))}
                </div>

                {/* Summer Semester (if needed) */}
                {parseInt(year) === 1 && (
                  <div className="flex flex-col space-y-4 min-w-[300px]">
                    <div className="text-center mb-4">
                      <h3 className="text-lg font-semibold text-green-600 bg-green-50 px-4 py-2 rounded-lg border border-green-200">
                        Summer 2025
                      </h3>
                    </div>
                    
                    {/* Add summer courses here if needed */}
                    <div className="bg-gray-100 border-2 border-dashed border-gray-300 rounded-lg p-4 text-center text-gray-500">
                      Optional Summer Courses
                    </div>
                  </div>
                )}
              </div>
            </motion.div>
          ))}
      </div>
    </div>
  );
};

export default PrerequisiteFlowchart;
