import React, { useRef, useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Course, getPrerequisites, getCorequisites } from '../data/courses';

interface FlowchartProps {
  selectedCourse: Course | null;
  onCourseClick: (course: Course) => void;
}

interface Connection {
  from: string;
  to: string;
  type: 'prerequisite' | 'corequisite';
}

const Flowchart: React.FC<FlowchartProps> = ({ selectedCourse, onCourseClick }) => {
  const svgRef = useRef<SVGSVGElement>(null);
  const [connections, setConnections] = useState<Connection[]>([]);
  const [coursePositions, setCoursePositions] = useState<{ [key: string]: { x: number; y: number } }>({});

  // Generate connections for the selected course
  useEffect(() => {
    if (!selectedCourse) {
      setConnections([]);
      return;
    }

    const newConnections: Connection[] = [];
    
    // Add prerequisite connections
    const prerequisites = getPrerequisites(selectedCourse.id);
    prerequisites.forEach(prereq => {
      newConnections.push({
        from: prereq.id,
        to: selectedCourse.id,
        type: 'prerequisite'
      });
    });

    // Add corequisite connections
    const corequisites = getCorequisites(selectedCourse.id);
    corequisites.forEach(coreq => {
      newConnections.push({
        from: coreq.id,
        to: selectedCourse.id,
        type: 'corequisite'
      });
    });

    setConnections(newConnections);
  }, [selectedCourse]);

  // Calculate positions for courses
  useEffect(() => {
    if (!selectedCourse) return;

    const positions: { [key: string]: { x: number; y: number } } = {};
    const centerX = 400;
    const centerY = 300;
    
    // Position selected course in center
    positions[selectedCourse.id] = { x: centerX, y: centerY };

    // Position prerequisites above
    const prerequisites = getPrerequisites(selectedCourse.id);
    const corequisites = getCorequisites(selectedCourse.id);
    
    const totalRelated = prerequisites.length + corequisites.length;
    const angleStep = totalRelated > 1 ? (Math.PI * 2) / totalRelated : 0;
    const radius = 150;

    let index = 0;
    
    // Position prerequisites
    prerequisites.forEach(prereq => {
      const angle = index * angleStep;
      positions[prereq.id] = {
        x: centerX + Math.cos(angle) * radius,
        y: centerY + Math.sin(angle) * radius - 50
      };
      index++;
    });

    // Position corequisites
    corequisites.forEach(coreq => {
      const angle = index * angleStep;
      positions[coreq.id] = {
        x: centerX + Math.cos(angle) * radius,
        y: centerY + Math.sin(angle) * radius - 50
      };
      index++;
    });

    setCoursePositions(positions);
  }, [selectedCourse]);

  const renderArrow = (from: string, to: string, type: 'prerequisite' | 'corequisite') => {
    const fromPos = coursePositions[from];
    const toPos = coursePositions[to];
    
    if (!fromPos || !toPos) return null;

    const dx = toPos.x - fromPos.x;
    const dy = toPos.y - fromPos.y;
    const angle = Math.atan2(dy, dx);
    
    // Adjust arrow start and end to avoid overlapping with course cards
    const offset = 60;
    const startX = fromPos.x + Math.cos(angle) * offset;
    const startY = fromPos.y + Math.sin(angle) * offset;
    const endX = toPos.x - Math.cos(angle) * offset;
    const endY = toPos.y - Math.sin(angle) * offset;

    const strokeColor = type === 'prerequisite' ? '#dc2626' : '#2563eb';
    const strokeWidth = type === 'prerequisite' ? 3 : 2;
    const dashArray = type === 'corequisite' ? '5,5' : 'none';

    return (
      <g key={`${from}-${to}`}>
        <line
          x1={startX}
          y1={startY}
          x2={endX}
          y2={endY}
          stroke={strokeColor}
          strokeWidth={strokeWidth}
          strokeDasharray={dashArray}
          markerEnd={`url(#arrowhead-${type})`}
        />
        <text
          x={(startX + endX) / 2}
          y={(startY + endY) / 2 - 10}
          textAnchor="middle"
          className="text-xs font-bold"
          fill={strokeColor}
        >
          {type === 'prerequisite' ? 'PREREQ' : 'COREQ'}
        </text>
      </g>
    );
  };

  const renderCourseNode = (course: Course) => {
    const position = coursePositions[course.id];
    if (!position) return null;

    const isSelected = selectedCourse?.id === course.id;

    return (
      <motion.g
        key={course.id}
        initial={{ opacity: 0, scale: 0 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5, delay: 0.1 }}
      >
        <rect
          x={position.x - 80}
          y={position.y - 40}
          width={160}
          height={80}
          rx={8}
          fill={isSelected ? '#fef3c7' : '#ffffff'}
          stroke={isSelected ? '#f59e0b' : '#000000'}
          strokeWidth={isSelected ? 3 : 2}
          className="cursor-pointer hover:stroke-primary-500 transition-colors"
          onClick={() => onCourseClick(course)}
        />
        <text
          x={position.x}
          y={position.y - 15}
          textAnchor="middle"
          className="text-sm font-bold"
          fill="#1f2937"
        >
          {course.code}
        </text>
        <text
          x={position.x}
          y={position.y + 5}
          textAnchor="middle"
          className="text-xs"
          fill="#6b7280"
        >
          {course.name.split(' ').slice(0, 2).join(' ')}
        </text>
        <text
          x={position.x}
          y={position.y + 20}
          textAnchor="middle"
          className="text-xs font-semibold"
          fill="#059669"
        >
          {course.units} units
        </text>
      </motion.g>
    );
  };

  if (!selectedCourse) {
    return (
      <div className="flex items-center justify-center h-96 bg-white border-2 border-black shadow-brutal">
        <div className="text-center">
          <div className="text-6xl mb-4">ðŸŽ“</div>
          <h3 className="text-xl font-bold text-gray-800 mb-2">Course Flowchart</h3>
          <p className="text-gray-600">Click on any course to see its relationships</p>
        </div>
      </div>
    );
  }

  const allCourses = [
    selectedCourse,
    ...getPrerequisites(selectedCourse.id),
    ...getCorequisites(selectedCourse.id)
  ];

  return (
    <div className="bg-white border-2 border-black shadow-brutal p-6">
      <h3 className="text-xl font-bold text-gray-800 mb-4 text-center">
        Course Relationships for {selectedCourse.code}
      </h3>
      
      <div className="relative w-full h-96 overflow-hidden">
        <svg
          ref={svgRef}
          width="100%"
          height="100%"
          viewBox="0 0 800 600"
          className="absolute inset-0"
        >
          {/* Arrow markers */}
          <defs>
            <marker
              id="arrowhead-prerequisite"
              markerWidth="10"
              markerHeight="7"
              refX="9"
              refY="3.5"
              orient="auto"
            >
              <polygon
                points="0 0, 10 3.5, 0 7"
                fill="#dc2626"
              />
            </marker>
            <marker
              id="arrowhead-corequisite"
              markerWidth="10"
              markerHeight="7"
              refX="9"
              refY="3.5"
              orient="auto"
            >
              <polygon
                points="0 0, 10 3.5, 0 7"
                fill="#2563eb"
              />
            </marker>
          </defs>

          {/* Render connections */}
          {connections.map(connection => 
            renderArrow(connection.from, connection.to, connection.type)
          )}

          {/* Render course nodes */}
          {allCourses.map(course => renderCourseNode(course))}
        </svg>
      </div>

      <div className="mt-4 flex justify-center gap-6 text-sm">
        <div className="flex items-center gap-2">
          <div className="w-4 h-0.5 bg-red-600"></div>
          <span className="font-semibold text-red-600">Prerequisites</span>
        </div>
        <div className="flex items-center gap-2">
          <div className="w-4 h-0.5 bg-blue-600 border-dashed border-blue-600"></div>
          <span className="font-semibold text-blue-600">Corequisites</span>
        </div>
      </div>
    </div>
  );
};

export default Flowchart;
