import React, { useRef, useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import { Course, getPrerequisites, getCorequisites, courses } from '../data/courses.ts';

interface FlowchartProps {
  selectedCourse: Course | null;
  onCourseClick: (course: Course) => void;
}

interface Connection {
  from: string;
  to: string;
  type: 'prerequisite' | 'corequisite';
}

const Flowchart: React.FC<FlowchartProps> = ({ selectedCourse, onCourseClick }) => {
  const svgRef = useRef<SVGSVGElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const [hoveredCourse, setHoveredCourse] = useState<Course | null>(null);
  const [connections, setConnections] = useState<Connection[]>([]);
  const [svgDimensions, setSvgDimensions] = useState({ width: 0, height: 0 });
  const [zoom, setZoom] = useState(1);
  const [pan, setPan] = useState({ x: 0, y: 0 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });

  // Update SVG dimensions on mount and resize
  useEffect(() => {
    const updateDimensions = () => {
      if (containerRef.current) {
        setSvgDimensions({
          width: containerRef.current.clientWidth,
          height: containerRef.current.clientHeight
        });
      }
    };

    updateDimensions();
    window.addEventListener('resize', updateDimensions);
    return () => window.removeEventListener('resize', updateDimensions);
  }, []);

  // Handle zoom with mouse wheel
  useEffect(() => {
    const handleWheel = (e: WheelEvent) => {
      e.preventDefault();
      const delta = e.deltaY > 0 ? 0.9 : 1.1;
      setZoom(prev => Math.max(0.3, Math.min(3, prev * delta)));
    };

    const container = containerRef.current;
    if (container) {
      container.addEventListener('wheel', handleWheel, { passive: false });
      return () => container.removeEventListener('wheel', handleWheel);
    }
  }, []);

  // Handle pan with mouse drag
  useEffect(() => {
    const handleMouseDown = (e: MouseEvent) => {
      setIsDragging(true);
      setDragStart({ x: e.clientX - pan.x, y: e.clientY - pan.y });
    };

    const handleMouseMove = (e: MouseEvent) => {
      if (isDragging) {
        setPan({
          x: e.clientX - dragStart.x,
          y: e.clientY - dragStart.y
        });
      }
    };

    const handleMouseUp = () => {
      setIsDragging(false);
    };

    const container = containerRef.current;
    if (container) {
      container.addEventListener('mousedown', handleMouseDown);
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      return () => {
        container.removeEventListener('mousedown', handleMouseDown);
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isDragging, pan, dragStart]);

  // Generate connections for the hovered course
  useEffect(() => {
    if (!hoveredCourse) {
      setConnections([]);
      return;
    }

    const newConnections: Connection[] = [];

    // Get prerequisites
    const prerequisites = getPrerequisites(hoveredCourse.id);
    prerequisites.forEach(prereq => {
      newConnections.push({
        from: prereq.id,
        to: hoveredCourse.id,
        type: 'prerequisite'
      });
    });

    // Get corequisites
    const corequisites = getCorequisites(hoveredCourse.id);
    corequisites.forEach(coreq => {
      newConnections.push({
        from: coreq.id,
        to: hoveredCourse.id,
        type: 'corequisite'
      });
    });

    setConnections(newConnections);
  }, [hoveredCourse]);

  // Get courses organized by year and semester
  const getCoursesByYearAndSemester = () => {
    const organized: { [key: string]: Course[] } = {};

    courses.forEach(course => {
      const semesterKey = `Year ${course.year} ${course.semester}`;
      if (!organized[semesterKey]) {
        organized[semesterKey] = [];
      }
      organized[semesterKey].push(course);
    });

    // Sort semesters in chronological order
    const sortedKeys = Object.keys(organized).sort((a, b) => {
      const yearA = parseInt(a.split(' ')[1]);
      const yearB = parseInt(b.split(' ')[1]);
      if (yearA !== yearB) return yearA - yearB;
      return a.includes('Fall') ? -1 : 1;
    });

    return sortedKeys.map(key => ({ semester: key, courses: organized[key] }));
  };

  const semesterData = getCoursesByYearAndSemester();
  const totalSemesters = semesterData.length;

  // Fixed sizing for better visibility
  const semesterWidth = 320;
  const courseHeight = 120;
  const courseSpacing = 20;
  const semesterSpacing = 80;
  const topMargin = 100;
  const leftMargin = 60;

  const svgWidth = totalSemesters * (semesterWidth + semesterSpacing) + leftMargin * 2;
  const svgHeight = Math.max(svgDimensions.height, 900);

  const getCoursePosition = (semesterIndex: number, courseIndex: number, totalCourses: number) => {
    const x = semesterIndex * (semesterWidth + semesterSpacing) + leftMargin;
    const totalHeight = totalCourses * (courseHeight + courseSpacing) - courseSpacing;
    const startY = topMargin + 80; // Extra space for semester title
    const y = startY + courseIndex * (courseHeight + courseSpacing);
    return { x, y };
  };

  const getSemesterTitlePosition = (semesterIndex: number) => {
    const x = semesterIndex * (semesterWidth + semesterSpacing) + leftMargin + semesterWidth / 2;
    return { x, y: topMargin };
  };

  const getCourseHighlightColor = (course: Course) => {
    if (!hoveredCourse) return null;

    // Check if this course is a prerequisite of the hovered course
    const isPrerequisite = connections.some(conn => 
      conn.from === course.id && conn.to === hoveredCourse.id && conn.type === 'prerequisite'
    );

    // Check if this course is a corequisite of the hovered course
    const isCorequisite = connections.some(conn => 
      conn.from === course.id && conn.to === hoveredCourse.id && conn.type === 'corequisite'
    );

    // Check if this course has the hovered course as a prerequisite
    const hasHoveredAsPrerequisite = getPrerequisites(course.id).some(prereq => prereq.id === hoveredCourse.id);

    if (isPrerequisite) return '#fef3c7'; // Yellow for prerequisites
    if (isCorequisite) return '#dbeafe'; // Blue for corequisites
    if (hasHoveredAsPrerequisite) return '#fce7f3'; // Pink for following courses
    if (course.id === hoveredCourse.id) return '#fef3c7'; // Yellow for hovered course

    return null;
  };

  const getCourseBorderColor = (course: Course) => {
    if (!hoveredCourse) return '#000000';

    const isPrerequisite = connections.some(conn => 
      conn.from === course.id && conn.to === hoveredCourse.id && conn.type === 'prerequisite'
    );

    const isCorequisite = connections.some(conn => 
      conn.from === course.id && conn.to === hoveredCourse.id && conn.type === 'corequisite'
    );

    const hasHoveredAsPrerequisite = getPrerequisites(course.id).some(prereq => prereq.id === hoveredCourse.id);

    if (isPrerequisite) return '#d97706'; // Orange border for prerequisites
    if (isCorequisite) return '#2563eb'; // Blue border for corequisites
    if (hasHoveredAsPrerequisite) return '#be185d'; // Pink border for following courses
    if (course.id === hoveredCourse.id) return '#d97706'; // Orange border for hovered course

    return '#000000';
  };

  const getConnectionPath = (fromCourse: Course, toCourse: Course, fromIndex: number, toIndex: number) => {
    const fromPos = getCoursePosition(fromIndex, fromCourse.year - 1, 1);
    const toPos = getCoursePosition(toIndex, toCourse.year - 1, 1);

    // Adjust arrow start and end to avoid overlapping with course cards
    const fromX = fromPos.x + semesterWidth - 20;
    const fromY = fromPos.y + courseHeight / 2;
    const toX = toPos.x + 20;
    const toY = toPos.y + courseHeight / 2;

    // Create a curved path
    const controlPoint1X = fromX + (toX - fromX) * 0.3;
    const controlPoint1Y = fromY;
    const controlPoint2X = fromX + (toX - fromX) * 0.7;
    const controlPoint2Y = toY;

    return `M ${fromX} ${fromY} C ${controlPoint1X} ${controlPoint1Y}, ${controlPoint2X} ${controlPoint2Y}, ${toX} ${toY}`;
  };

  return (
    <div 
      ref={containerRef}
      className="w-full h-full overflow-hidden bg-gradient-to-br from-blue-50 via-indigo-50 to-purple-50 cursor-grab active:cursor-grabbing"
    >
      <svg
        ref={svgRef}
        width={svgWidth}
        height={svgHeight}
        className="w-full h-full"
        style={{
          transform: `translate(${pan.x}px, ${pan.y}px) scale(${zoom})`,
          transformOrigin: '0 0',
          transition: isDragging ? 'none' : 'transform 0.1s ease-out'
        }}
      >
        {/* Arrow markers for connections */}
        <defs>
          <marker
            id="arrowhead-prerequisite"
            markerWidth="12"
            markerHeight="8"
            refX="10"
            refY="4"
            orient="auto"
          >
            <polygon points="0 0, 12 4, 0 8" fill="#d97706" />
          </marker>
          <marker
            id="arrowhead-corequisite"
            markerWidth="12"
            markerHeight="8"
            refX="10"
            refY="4"
            orient="auto"
          >
            <polygon points="0 0, 12 4, 0 8" fill="#2563eb" />
          </marker>
          <marker
            id="arrowhead-following"
            markerWidth="12"
            markerHeight="8"
            refX="10"
            refY="4"
            orient="auto"
          >
            <polygon points="0 0, 12 4, 0 8" fill="#be185d" />
          </marker>
        </defs>

        {/* Background grid */}
        <defs>
          <pattern id="grid" width="40" height="40" patternUnits="userSpaceOnUse">
            <path d="M 40 0 L 0 0 0 40" fill="none" stroke="#e2e8f0" strokeWidth="1" opacity="0.3"/>
          </pattern>
        </defs>
        <rect width="100%" height="100%" fill="url(#grid)" />

        {/* Semester titles */}
        {semesterData.map((semester, semesterIndex) => {
          const titlePos = getSemesterTitlePosition(semesterIndex);
          return (
            <g key={semester.semester}>
              {/* Semester background */}
              <rect
                x={titlePos.x - 120}
                y={titlePos.y - 30}
                width="240"
                height="40"
                rx="20"
                fill="rgba(255, 255, 255, 0.9)"
                stroke="#000000"
                strokeWidth="2"
                className="shadow-lg"
              />
              <text
                x={titlePos.x}
                y={titlePos.y - 5}
                textAnchor="middle"
                className="text-lg font-bold fill-gray-900"
              >
                {semester.semester}
              </text>
            </g>
          );
        })}

        {/* Connection arrows - only shown on hover */}
        {connections.map((connection, index) => {
          const fromCourse = courses.find(c => c.id === connection.from);
          const toCourse = courses.find(c => c.id === connection.to);

          if (!fromCourse || !toCourse) return null;

          const fromSemesterIndex = semesterData.findIndex(s => 
            s.courses.some(c => c.id === fromCourse.id)
          );
          const toSemesterIndex = semesterData.findIndex(s => 
            s.courses.some(c => c.id === toCourse.id)
          );

          if (fromSemesterIndex === -1 || toSemesterIndex === -1) return null;

          const path = getConnectionPath(fromCourse, toCourse, fromSemesterIndex, toSemesterIndex);
          const isPrerequisite = connection.type === 'prerequisite';

          return (
            <g key={`connection-${index}`}>
              <path
                d={path}
                stroke={isPrerequisite ? "#d97706" : "#2563eb"}
                strokeWidth="4"
                strokeDasharray={isPrerequisite ? "none" : "12,6"}
                fill="none"
                markerEnd={`url(#arrowhead-${connection.type})`}
                opacity="0.8"
                className="transition-all duration-300"
              />
            </g>
          );
        })}

        {/* Course cards */}
        {semesterData.map((semester, semesterIndex) => (
          <g key={semester.semester}>
            {semester.courses.map((course, courseIndex) => {
              const position = getCoursePosition(semesterIndex, courseIndex, semester.courses.length);
              const highlightColor = getCourseHighlightColor(course);
              const borderColor = getCourseBorderColor(course);

              return (
                <g key={course.id}>
                  {/* Course card background */}
                  <rect
                    x={position.x}
                    y={position.y}
                    width={semesterWidth - 40}
                    height={courseHeight}
                    rx="16"
                    fill={highlightColor || "#ffffff"}
                    stroke={borderColor}
                    strokeWidth="3"
                    className="cursor-pointer transition-all duration-200 shadow-lg hover:shadow-xl"
                    onMouseEnter={() => setHoveredCourse(course)}
                    onMouseLeave={() => setHoveredCourse(null)}
                    onClick={() => onCourseClick(course)}
                  />

                  {/* Course code */}
                  <text
                    x={position.x + 20}
                    y={position.y + 32}
                    className="text-lg font-bold fill-gray-900"
                  >
                    {course.code}
                  </text>

                  {/* Course name - with text wrapping */}
                  <foreignObject
                    x={position.x + 20}
                    y={position.y + 45}
                    width={semesterWidth - 80}
                    height={courseHeight - 80}
                  >
                    <div className="text-sm text-gray-700 leading-tight font-medium">
                      {course.name}
                    </div>
                  </foreignObject>

                  {/* Units */}
                  <text
                    x={position.x + 20}
                    y={position.y + courseHeight - 15}
                    className="text-sm font-medium fill-gray-500"
                  >
                    {course.units} units
                  </text>
                </g>
              );
            })}
          </g>
        ))}

        {/* Legend */}
        {hoveredCourse && (
          <g>
            <rect
              x="20"
              y="20"
              width="300"
              height="120"
              rx="12"
              fill="rgba(255, 255, 255, 0.95)"
              stroke="#000000"
              strokeWidth="2"
              className="shadow-lg"
            />
            <text x="40" y="45" className="text-sm font-bold fill-gray-900">
              Course Relationships
            </text>
            <rect x="40" y="55" width="16" height="16" fill="#fef3c7" stroke="#d97706" strokeWidth="2" rx="2"/>
            <text x="65" y="67" className="text-xs fill-gray-700">Prerequisites</text>
            <rect x="40" y="75" width="16" height="16" fill="#dbeafe" stroke="#2563eb" strokeWidth="2" rx="2"/>
            <text x="65" y="87" className="text-xs fill-gray-700">Corequisites</text>
            <rect x="40" y="95" width="16" height="16" fill="#fce7f3" stroke="#be185d" strokeWidth="2" rx="2"/>
            <text x="65" y="107" className="text-xs fill-gray-700">Following Courses</text>
          </g>
        )}

        {/* Zoom controls */}
        <g>
          <rect
            x={svgWidth - 120}
            y="20"
            width="100"
            height="60"
            rx="8"
            fill="rgba(255, 255, 255, 0.9)"
            stroke="#000000"
            strokeWidth="2"
            className="shadow-lg"
          />
          <text x={svgWidth - 70} y="40" textAnchor="middle" className="text-xs font-bold fill-gray-900">
            Zoom: {Math.round(zoom * 100)}%
          </text>
          <text x={svgWidth - 70} y="55" textAnchor="middle" className="text-xs fill-gray-600">
            Scroll to zoom
          </text>
          <text x={svgWidth - 70} y="70" textAnchor="middle" className="text-xs fill-gray-600">
            Drag to pan
          </text>
        </g>
      </svg>
    </div>
  );
};

export default Flowchart;
