import React from 'react';
import { motion } from 'framer-motion';
import { Course } from '../types';
import CourseCard from './CourseCard';
import { ArrowRight, ArrowDown, ArrowUpRight } from 'lucide-react';

interface FlowchartLayoutProps {
  courses: Course[];
  onCourseClick: (course: Course) => void;
  selectedCourse: Course | null;
}

const FlowchartLayout: React.FC<FlowchartLayoutProps> = ({
  courses,
  onCourseClick,
  selectedCourse,
}) => {
  // Group courses by year and semester
  const getCoursesByYear = () => {
    const years: { [key: number]: { fall: Course[], winter: Course[] } } = {};
    
    courses.forEach(course => {
      if (!years[course.year]) {
        years[course.year] = { fall: [], winter: [] };
      }
      
      if (course.semester === 'Fall') {
        years[course.year].fall.push(course);
      } else {
        years[course.year].winter.push(course);
      }
    });

    return years;
  };

  const yearsData = getCoursesByYear();

  const renderArrow = (type: 'horizontal' | 'vertical' | 'diagonal') => {
    const arrowClasses = "text-gray-400 stroke-2";
    
    switch (type) {
      case 'horizontal':
        return <ArrowRight className={`w-6 h-6 ${arrowClasses}`} />;
      case 'vertical':
        return <ArrowDown className={`w-6 h-6 ${arrowClasses}`} />;
      case 'diagonal':
        return <ArrowUpRight className={`w-6 h-6 ${arrowClasses}`} />;
      default:
        return <ArrowRight className={`w-6 h-6 ${arrowClasses}`} />;
    }
  };

  const renderPrerequisiteArrows = (course: Course) => {
    if (course.prerequisites.length === 0) return null;

    return (
      <div className="absolute -top-3 left-1/2 transform -translate-x-1/2">
        <div className="flex items-center space-x-1">
          {course.prerequisites.map((prereq, index) => (
            <div key={index} className="flex items-center">
              <div className="w-2 h-2 bg-green-500 rounded-full"></div>
              {index < course.prerequisites.length - 1 && (
                <div className="w-4 h-0.5 bg-green-500"></div>
              )}
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="space-y-12">
      {Object.entries(yearsData)
        .sort(([a], [b]) => parseInt(a) - parseInt(b))
        .map(([year, { fall, winter }]) => (
          <motion.div
            key={year}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="relative"
          >
            {/* Year Header */}
            <div className="text-center mb-8">
              <h2 className="text-3xl font-bold text-gray-900 bg-white px-6 py-2 rounded-full shadow-sm inline-block">
                Year {year}
              </h2>
            </div>

            <div className="space-y-16">
              {/* Fall Semester */}
              <div className="relative">
                <div className="text-center mb-6">
                  <h3 className="text-xl font-semibold text-orange-600 bg-orange-50 px-4 py-2 rounded-full inline-block">
                    Fall Semester
                  </h3>
                </div>
                
                <div className="flex items-center justify-center space-x-8 flex-wrap">
                  {fall.map((course, index) => (
                    <div key={course.id} className="relative">
                      {/* Prerequisite arrows */}
                      {renderPrerequisiteArrows(course)}
                      
                      {/* Course card */}
                      <div className="w-64">
                        <CourseCard
                          course={course}
                          onClick={onCourseClick}
                          isSelected={selectedCourse?.id === course.id}
                        />
                      </div>
                      
                      {/* Arrow to next course */}
                      {index < fall.length - 1 && (
                        <div className="absolute top-1/2 -right-4 transform -translate-y-1/2">
                          {renderArrow('horizontal')}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>

              {/* Vertical arrow to Winter */}
              <div className="flex justify-center">
                <div className="flex flex-col items-center space-y-2">
                  {renderArrow('vertical')}
                  <span className="text-sm text-gray-500 font-medium">Next Semester</span>
                </div>
              </div>

              {/* Winter Semester */}
              <div className="relative">
                <div className="text-center mb-6">
                  <h3 className="text-xl font-semibold text-blue-600 bg-blue-50 px-4 py-2 rounded-full inline-block">
                    Winter Semester
                  </h3>
                </div>
                
                <div className="flex items-center justify-center space-x-8 flex-wrap">
                  {winter.map((course, index) => (
                    <div key={course.id} className="relative">
                      {/* Prerequisite arrows */}
                      {renderPrerequisiteArrows(course)}
                      
                      {/* Course card */}
                      <div className="w-64">
                        <CourseCard
                          course={course}
                          onClick={onCourseClick}
                          isSelected={selectedCourse?.id === course.id}
                        />
                      </div>
                      
                      {/* Arrow to next course */}
                      {index < winter.length - 1 && (
                        <div className="absolute top-1/2 -right-4 transform -translate-y-1/2">
                          {renderArrow('horizontal')}
                        </div>
                      )}
                    </div>
                  ))}
                </div>
              </div>

              {/* Arrow to next year (except for last year) */}
              {parseInt(year) < Math.max(...Object.keys(yearsData).map(Number)) && (
                <div className="flex justify-center mt-8">
                  <div className="flex flex-col items-center space-y-2">
                    {renderArrow('vertical')}
                    <span className="text-sm text-gray-500 font-medium">Next Year</span>
                  </div>
                </div>
              )}
            </div>
          </motion.div>
        ))}
    </div>
  );
};

export default FlowchartLayout;
