import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { GraduationCap, BookOpen, Filter, Search, ArrowRight, Calendar, Users, Grid, GitBranch } from 'lucide-react';
import CourseCard from './components/CourseCard.tsx';
import CourseModal from './components/CourseModal.tsx';
import Flowchart from './components/Flowchart.tsx';
import { courses, getCoursesByYearAndSemester, Course } from './data/courses.ts';

function App() {
  const [selectedCourse, setSelectedCourse] = useState<Course | null>(null);
  const [modalCourse, setModalCourse] = useState<Course | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedYear, setSelectedYear] = useState<number | 'all'>('all');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');
  const [currentView, setCurrentView] = useState<'flowchart' | 'browser'>('flowchart');

  const handleCourseClick = (course: Course) => {
    setModalCourse(course);
    setIsModalOpen(true);
  };

  const handleFlowchartCourseClick = (course: Course) => {
    setSelectedCourse(course);
    handleCourseClick(course);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalCourse(null);
  };

  const filteredCourses = courses.filter(course => {
    const matchesSearch = course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         course.code.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         course.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesYear = selectedYear === 'all' || course.year === selectedYear;
    const matchesCategory = selectedCategory === 'all' || course.category === selectedCategory;
    return matchesSearch && matchesYear && matchesCategory;
  });

  const years = [1, 2, 3, 4];
  const categories = ['Core', 'Technical', 'Elective', 'Lab', 'Project'];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
      {/* Header */}
      <header className="bg-white border-b-4 border-black shadow-brutal">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="p-3 bg-primary rounded-lg shadow-brutal">
                <GraduationCap className="w-8 h-8 text-white" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Electrical Engineering</h1>
                <p className="text-gray-600">University of Ottawa - Course Timeline</p>
              </div>
            </div>

            {/* View Toggle */}
            <div className="flex bg-gray-100 rounded-lg p-1 shadow-brutal">
              <button
                onClick={() => setCurrentView('flowchart')}
                className={`flex items-center space-x-2 px-4 py-2 rounded-md font-medium transition-all ${
                  currentView === 'flowchart'
                    ? 'bg-white text-primary shadow-brutal'
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                <GitBranch className="w-4 h-4" />
                <span>Timeline</span>
              </button>
              <button
                onClick={() => setCurrentView('browser')}
                className={`flex items-center space-x-2 px-4 py-2 rounded-md font-medium transition-all ${
                  currentView === 'browser'
                    ? 'bg-white text-primary shadow-brutal'
                    : 'text-gray-600 hover:text-gray-900'
                }`}
              >
                <Grid className="w-4 h-4" />
                <span>Browser</span>
              </button>
            </div>
          </div>
        </div>
      </header>

      <main className="max-w-7xl mx-auto px-6 py-8">
        {currentView === 'flowchart' ? (
          /* Flowchart Timeline View */
          <div className="space-y-6">
            <div className="text-center">
              <h2 className="text-xl font-bold text-gray-900 mb-1">Course Timeline</h2>
              <p className="text-sm text-gray-600">Hover over courses to see prerequisites and corequisites</p>
            </div>

            {/* Main Flowchart - Much larger */}
            <div className="bg-white rounded-xl border-4 border-black shadow-brutal-lg p-4 h-[80vh]">
              <Flowchart 
                selectedCourse={selectedCourse} 
                onCourseClick={handleFlowchartCourseClick}
              />
            </div>
          </div>
        ) : (
          /* Course Browser View */
          <div className="space-y-8">
            <div className="text-center">
              <h2 className="text-3xl font-bold text-gray-900 mb-2">Course Browser</h2>
              <p className="text-gray-600">Search and filter through all courses</p>
            </div>

            {/* Search and Filters */}
            <div className="bg-white rounded-xl border-4 border-black shadow-brutal-lg p-6">
              <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                {/* Search */}
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                  <input
                    type="text"
                    placeholder="Search courses..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full pl-10 pr-4 py-3 border-2 border-gray-300 rounded-lg focus:border-primary focus:outline-none shadow-brutal"
                  />
                </div>

                {/* Year Filter */}
                <select
                  value={selectedYear}
                  onChange={(e) => setSelectedYear(e.target.value as number | 'all')}
                  className="px-4 py-3 border-2 border-gray-300 rounded-lg focus:border-primary focus:outline-none shadow-brutal"
                >
                  <option value="all">All Years</option>
                  {years.map(year => (
                    <option key={year} value={year}>Year {year}</option>
                  ))}
                </select>

                {/* Category Filter */}
                <select
                  value={selectedCategory}
                  onChange={(e) => setSelectedCategory(e.target.value)}
                  className="px-4 py-3 border-2 border-gray-300 rounded-lg focus:border-primary focus:outline-none shadow-brutal"
                >
                  <option value="all">All Categories</option>
                  {categories.map(category => (
                    <option key={category} value={category}>{category}</option>
                  ))}
                </select>

                {/* Results Count */}
                <div className="flex items-center justify-center px-4 py-3 bg-gray-50 border-2 border-gray-300 rounded-lg shadow-brutal">
                  <span className="text-gray-600 font-medium">
                    {filteredCourses.length} courses
                  </span>
                </div>
              </div>
            </div>

            {/* Course Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredCourses.map(course => (
                <CourseCard
                  key={course.id}
                  course={course}
                  onClick={handleCourseClick}
                />
              ))}
            </div>

            {filteredCourses.length === 0 && (
              <div className="text-center py-12">
                <BookOpen className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                <h3 className="text-xl font-semibold text-gray-600 mb-2">No courses found</h3>
                <p className="text-gray-500">Try adjusting your search or filters</p>
              </div>
            )}
          </div>
        )}
      </main>

      {/* Course Modal */}
      {isModalOpen && modalCourse && (
        <CourseModal
          course={modalCourse}
          onClose={closeModal}
        />
      )}
    </div>
  );
}

export default App;
