import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { courses } from './data/courses';
import { Course } from './types';
import Header from './components/Header';
import Legend from './components/Legend';
import YearSection from './components/YearSection';
import CourseModal from './components/CourseModal';
import { Search, Filter, X } from 'lucide-react';

const App: React.FC = () => {
  const [selectedCourse, setSelectedCourse] = useState<Course | null>(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState<string>('All');
  const [showLegend, setShowLegend] = useState(true);

  // Filter courses based on search term and category
  const filteredCourses = courses.filter(course => {
    const matchesSearch = course.code.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         course.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesCategory = selectedCategory === 'All' || course.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  // Group courses by year and semester
  const getCoursesByYear = () => {
    const years: { [key: number]: { fall: Course[], winter: Course[] } } = {};
    
    filteredCourses.forEach(course => {
      if (!years[course.year]) {
        years[course.year] = { fall: [], winter: [] };
      }
      
      if (course.semester === 'Fall') {
        years[course.year].fall.push(course);
      } else {
        years[course.year].winter.push(course);
      }
    });

    return years;
  };

  const yearsData = getCoursesByYear();

  const handleCourseClick = (course: Course) => {
    setSelectedCourse(course);
  };

  const handleCloseModal = () => {
    setSelectedCourse(null);
  };

  const categories = ['All', 'Core', 'Technical', 'Elective', 'Complementary'];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-blue-50">
      <Header />
      
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Search and Filter Controls */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white rounded-lg shadow-sm border border-gray-200 p-4 mb-6"
        >
          <div className="flex flex-col md:flex-row gap-4">
            {/* Search */}
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search courses by code or name..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                />
              </div>
            </div>

            {/* Category Filter */}
            <div className="flex items-center space-x-2">
              <Filter className="w-5 h-5 text-gray-400" />
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              >
                {categories.map(category => (
                  <option key={category} value={category}>
                    {category}
                  </option>
                ))}
              </select>
            </div>

            {/* Toggle Legend */}
            <button
              onClick={() => setShowLegend(!showLegend)}
              className="px-4 py-2 bg-primary-100 text-primary-700 rounded-lg hover:bg-primary-200 transition-colors"
            >
              {showLegend ? 'Hide' : 'Show'} Legend
            </button>
          </div>

          {/* Results Count */}
          <div className="mt-4 pt-4 border-t border-gray-200">
            <p className="text-sm text-gray-600">
              Showing {filteredCourses.length} of {courses.length} courses
              {searchTerm && ` matching "${searchTerm}"`}
              {selectedCategory !== 'All' && ` in ${selectedCategory} category`}
            </p>
          </div>
        </motion.div>

        {/* Legend */}
        {showLegend && <Legend />}

        {/* Course Flowchart */}
        <div className="space-y-8">
          {Object.keys(yearsData).length > 0 ? (
            Object.entries(yearsData)
              .sort(([a], [b]) => parseInt(a) - parseInt(b))
              .map(([year, { fall, winter }]) => (
                <YearSection
                  key={year}
                  year={parseInt(year)}
                  fallCourses={fall}
                  winterCourses={winter}
                  onCourseClick={handleCourseClick}
                  selectedCourse={selectedCourse}
                />
              ))
          ) : (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="text-center py-12"
            >
              <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-8">
                <X className="w-12 h-12 text-gray-400 mx-auto mb-4" />
                <h3 className="text-lg font-medium text-gray-900 mb-2">No courses found</h3>
                <p className="text-gray-600">
                  Try adjusting your search terms or category filter to find courses.
                </p>
              </div>
            </motion.div>
          )}
        </div>

        {/* Course Modal */}
        <CourseModal
          course={selectedCourse}
          onClose={handleCloseModal}
          allCourses={courses}
        />
      </main>

      {/* Footer */}
      <footer className="bg-white border-t border-gray-200 mt-12">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
          <div className="text-center text-sm text-gray-600">
            <p>
              University of Ottawa Electrical Engineering Program • 
              Interactive Course Flowchart • 
              Total Units: {courses.reduce((sum, course) => sum + course.units, 0)}
            </p>
            <p className="mt-2">
              Click on any course to view detailed information including prerequisites and descriptions.
            </p>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default App;
