import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { GraduationCap, BookOpen, Filter, Search, ArrowRight, Calendar, Users } from 'lucide-react';
import CourseCard from './components/CourseCard.tsx';
import CourseModal from './components/CourseModal.tsx';
import Flowchart from './components/Flowchart.tsx';
import { courses, getCoursesByYearAndSemester, Course } from './data/courses.ts';

function App() {
  const [selectedCourse, setSelectedCourse] = useState<Course | null>(null);
  const [modalCourse, setModalCourse] = useState<Course | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedYear, setSelectedYear] = useState<number | 'all'>('all');
  const [selectedCategory, setSelectedCategory] = useState<string>('all');

  const handleCourseClick = (course: Course) => {
    setSelectedCourse(course);
  };

  const handleCourseCardClick = (course: Course) => {
    setModalCourse(course);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalCourse(null);
  };

  const filteredCourses = courses.filter(course => {
    const matchesSearch = course.code.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         course.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         course.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesYear = selectedYear === 'all' || course.year === selectedYear;
    const matchesCategory = selectedCategory === 'all' || course.category === selectedCategory;

    return matchesSearch && matchesYear && matchesCategory;
  });

  const totalUnits = courses.reduce((sum, course) => sum + course.units, 0);
  const coreUnits = courses.filter(c => c.category === 'Core').reduce((sum, course) => sum + course.units, 0);
  const technicalUnits = courses.filter(c => c.category === 'Technical').reduce((sum, course) => sum + course.units, 0);

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100">
      {/* Header */}
      <header className="bg-white border-b-2 border-black shadow-brutal">
        <div className="max-w-7xl mx-auto px-6 py-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <div className="text-4xl">‚ö°</div>
              <div>
                <h1 className="text-3xl font-bold text-gray-900">Electrical Engineering</h1>
                <p className="text-lg text-gray-600">University of Ottawa - Course Flowchart</p>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <div className="text-right">
                <div className="text-2xl font-bold text-primary-600">{totalUnits}</div>
                <div className="text-sm text-gray-600">Total Units</div>
              </div>
              <GraduationCap size={32} className="text-primary-500" />
            </div>
          </div>
        </div>
      </header>

      {/* Stats Bar */}
      <div className="bg-white border-b-2 border-black shadow-brutal">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="flex items-center gap-3 p-3 bg-primary-50 border-2 border-primary-200 rounded shadow-brutal">
              <BookOpen size={20} className="text-primary-600" />
              <div>
                <div className="font-bold text-primary-800">{courses.length}</div>
                <div className="text-sm text-primary-600">Total Courses</div>
              </div>
            </div>
            <div className="flex items-center gap-3 p-3 bg-green-50 border-2 border-green-200 rounded shadow-brutal">
              <Calendar size={20} className="text-green-600" />
              <div>
                <div className="font-bold text-green-800">{coreUnits}</div>
                <div className="text-sm text-green-600">Core Units</div>
              </div>
            </div>
            <div className="flex items-center gap-3 p-3 bg-blue-50 border-2 border-blue-200 rounded shadow-brutal">
              <Users size={20} className="text-blue-600" />
              <div>
                <div className="font-bold text-blue-800">{technicalUnits}</div>
                <div className="text-sm text-blue-600">Technical Units</div>
              </div>
            </div>
            <div className="flex items-center gap-3 p-3 bg-purple-50 border-2 border-purple-200 rounded shadow-brutal">
              <ArrowRight size={20} className="text-purple-600" />
              <div>
                <div className="font-bold text-purple-800">4</div>
                <div className="text-sm text-purple-600">Years</div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-6 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Left Column - Course List */}
          <div className="lg:col-span-2">
            {/* Filters */}
            <div className="bg-white border-2 border-black shadow-brutal p-6 mb-8">
              <div className="flex items-center gap-4 mb-4">
                <Filter size={20} className="text-gray-600" />
                <h2 className="text-xl font-bold text-gray-800">Filters</h2>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {/* Search */}
                <div className="relative">
                  <Search size={16} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                  <input
                    type="text"
                    placeholder="Search courses..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full pl-10 pr-4 py-2 border-2 border-black rounded shadow-brutal focus:outline-none focus:ring-2 focus:ring-primary-500"
                  />
                </div>

                {/* Year Filter */}
                <select
                  value={selectedYear}
                  onChange={(e) => setSelectedYear(e.target.value === 'all' ? 'all' : parseInt(e.target.value))}
                  className="px-4 py-2 border-2 border-black rounded shadow-brutal focus:outline-none focus:ring-2 focus:ring-primary-500"
                >
                  <option value="all">All Years</option>
                  <option value={1}>Year 1</option>
                  <option value={2}>Year 2</option>
                  <option value={3}>Year 3</option>
                  <option value={4}>Year 4</option>
                </select>

                {/* Category Filter */}
                <select
                  value={selectedCategory}
                  onChange={(e) => setSelectedCategory(e.target.value)}
                  className="px-4 py-2 border-2 border-black rounded shadow-brutal focus:outline-none focus:ring-2 focus:ring-primary-500"
                >
                  <option value="all">All Categories</option>
                  <option value="Core">Core</option>
                  <option value="Technical">Technical</option>
                  <option value="Complementary">Complementary</option>
                  <option value="Elective">Elective</option>
                </select>
              </div>

              <div className="mt-4 text-sm text-gray-600">
                Showing {filteredCourses.length} of {courses.length} courses
              </div>
            </div>

            {/* Course Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {filteredCourses.map((course) => (
                <CourseCard
                  key={course.id}
                  course={course}
                  onClick={handleCourseCardClick}
                  isSelected={selectedCourse?.id === course.id}
                />
              ))}
            </div>

            {filteredCourses.length === 0 && (
              <div className="text-center py-12">
                <div className="text-6xl mb-4">üîç</div>
                <h3 className="text-xl font-bold text-gray-800 mb-2">No courses found</h3>
                <p className="text-gray-600">Try adjusting your search or filters</p>
              </div>
            )}
          </div>

          {/* Right Column - Flowchart */}
          <div className="lg:col-span-1">
            <Flowchart
              selectedCourse={selectedCourse}
              onCourseClick={handleCourseClick}
            />
          </div>
        </div>

        {/* Year-by-Year Breakdown */}
        <div className="mt-12">
          <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">Program Structure</h2>

          {[1, 2, 3, 4].map((year) => (
            <div key={year} className="mb-8">
              <div className="year-header">
                Year {year} - {year === 1 ? '30' : year === 2 ? '33' : '30'} Units
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
                {/* Fall Semester */}
                <div className="semester-container">
                  <h3 className="semester-title">Fall Semester</h3>
                  <div className="grid gap-4">
                    {getCoursesByYearAndSemester(year, 'Fall').map((course) => (
                      <CourseCard
                        key={course.id}
                        course={course}
                        onClick={handleCourseCardClick}
                        isSelected={selectedCourse?.id === course.id}
                      />
                    ))}
                  </div>
                </div>

                {/* Winter Semester */}
                <div className="semester-container">
                  <h3 className="semester-title">Winter Semester</h3>
                  <div className="grid gap-4">
                    {getCoursesByYearAndSemester(year, 'Winter').map((course) => (
                      <CourseCard
                        key={course.id}
                        course={course}
                        onClick={handleCourseCardClick}
                        isSelected={selectedCourse?.id === course.id}
                      />
                    ))}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </main>

      {/* Course Modal */}
      <CourseModal
        course={modalCourse}
        isOpen={isModalOpen}
        onClose={closeModal}
      />
    </div>
  );
}

export default App;
