import React, { useState } from 'react';
import { Course, getPrerequisites, getCorequisites, courses } from '../data/courses.ts';

interface FlowchartProps {
  selectedCourse: Course | null;
  onCourseClick: (course: Course) => void;
}

const Flowchart: React.FC<FlowchartProps> = ({ selectedCourse, onCourseClick }) => {
  const [hoveredCourse, setHoveredCourse] = useState<Course | null>(null);
  const [offScreenCourses, setOffScreenCourses] = useState<{
    left: Course[];
    right: Course[];
  }>({ left: [], right: [] });

  // Get courses organized by year and semester
  const getCoursesByYearAndSemester = () => {
    const organized: { [key: string]: Course[] } = {};

    courses.forEach(course => {
      const semesterKey = `Year ${course.year} ${course.semester}`;
      if (!organized[semesterKey]) {
        organized[semesterKey] = [];
      }
      organized[semesterKey].push(course);
    });

    // Sort semesters in chronological order
    const sortedKeys = Object.keys(organized).sort((a, b) => {
      const yearA = parseInt(a.split(' ')[1]);
      const yearB = parseInt(b.split(' ')[1]);
      if (yearA !== yearB) return yearA - yearB;
      return a.includes('Fall') ? -1 : 1;
    });

    return sortedKeys.map(key => ({ semester: key, courses: organized[key] }));
  };

  const semesterData = getCoursesByYearAndSemester();

  // Check for courses that are off-screen when hovering
  const checkOffScreenCourses = (course: Course) => {
    if (!course) return;

    const allRelatedCourses = [
      ...getPrerequisites(course.id),
      ...getCorequisites(course.id),
      ...courses.filter(c => getPrerequisites(c.id).some(prereq => prereq.id === course.id))
    ];

    const courseIndex = semesterData.findIndex(semester => 
      semester.courses.some(c => c.id === course.id)
    );

    const leftCourses = allRelatedCourses.filter(relatedCourse => {
      const relatedIndex = semesterData.findIndex(semester => 
        semester.courses.some(c => c.id === relatedCourse.id)
      );
      return relatedIndex < courseIndex;
    });

    const rightCourses = allRelatedCourses.filter(relatedCourse => {
      const relatedIndex = semesterData.findIndex(semester => 
        semester.courses.some(c => c.id === relatedCourse.id)
      );
      return relatedIndex > courseIndex;
    });

    setOffScreenCourses({ left: leftCourses, right: rightCourses });
  };

  const getCourseHighlightColor = (course: Course) => {
    if (!hoveredCourse) return 'bg-white border-2 border-black shadow-brutal';

    // Check if this course is a prerequisite of the hovered course
    const prerequisites = getPrerequisites(hoveredCourse.id);
    const isPrerequisite = prerequisites.some(prereq => prereq.id === course.id);

    // Check if this course is a corequisite of the hovered course
    const corequisites = getCorequisites(hoveredCourse.id);
    const isCorequisite = corequisites.some(coreq => coreq.id === course.id);

    // Check if this course has the hovered course as a prerequisite
    const hasHoveredAsPrerequisite = getPrerequisites(course.id).some(prereq => prereq.id === hoveredCourse.id);

    if (isPrerequisite) return 'bg-yellow-200 border-2 border-orange-600 shadow-brutal';
    if (isCorequisite) return 'bg-blue-200 border-2 border-blue-600 shadow-brutal';
    if (hasHoveredAsPrerequisite) return 'bg-pink-200 border-2 border-pink-600 shadow-brutal';
    if (course.id === hoveredCourse.id) return 'bg-green-200 border-2 border-green-600 shadow-brutal-lg';

    return 'bg-white border-2 border-black shadow-brutal';
  };

  return (
    <div 
      className="w-full h-full overflow-auto bg-gradient-to-br from-yellow-100 via-orange-100 to-red-100"
      style={{
        backgroundImage: `
          radial-gradient(circle, #000 1px, transparent 1px)
        `,
        backgroundSize: '20px 20px',
        backgroundPosition: '0 0, 10px 10px'
      }}
    >
      {/* Header with Legend */}
      <div className="sticky top-0 z-10 bg-white border-b-4 border-black shadow-brutal">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-lg font-black text-black mb-1">Course Timeline</h2>
              <p className="text-sm font-bold text-black">Hover over courses to see relationships</p>
            </div>

            {/* Legend */}
            <div className="flex items-center space-x-6">
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-yellow-200 border border-orange-600"></div>
                <span className="text-xs font-bold text-black">Prerequisites</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-blue-200 border border-blue-600"></div>
                <span className="text-xs font-bold text-black">Corequisites</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-pink-200 border border-pink-600"></div>
                <span className="text-xs font-bold text-black">Following Courses</span>
              </div>
              <div className="flex items-center space-x-2">
                <div className="w-3 h-3 bg-green-200 border border-green-600"></div>
                <span className="text-xs font-bold text-black">Selected Course</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Flowchart Content */}
      <div className="p-8">
        <div className="flex gap-12 min-w-max">
          {semesterData.map((semester) => (
            <div key={semester.semester} className="flex-shrink-0">
              {/* Semester Header */}
              <div className="text-center mb-8">
                <div className="inline-flex items-center px-8 py-4 bg-white rounded-none border-4 border-black shadow-brutal">
                  <h3 className="text-xl font-black text-black">{semester.semester}</h3>
                </div>
              </div>

              {/* Course Cards */}
              <div className="space-y-4">
                {semester.courses.map((course) => {
                  const highlightClass = getCourseHighlightColor(course);

                  return (
                    <div
                      key={course.id}
                      className={`w-64 p-4 rounded-none border-2 cursor-pointer transition-all duration-200 hover:shadow-brutal-lg hover:scale-[1.02] ${highlightClass}`}
                      onMouseEnter={() => {
                        setHoveredCourse(course);
                        checkOffScreenCourses(course);
                      }}
                      onMouseLeave={() => {
                        setHoveredCourse(null);
                        setOffScreenCourses({ left: [], right: [] });
                      }}
                      onClick={() => onCourseClick(course)}
                    >
                      <div className="flex items-start justify-between mb-3">
                        <div className="font-bold text-black text-lg">{course.code}</div>
                        <div className="text-xs font-bold text-black bg-gray-200 px-2 py-1 border border-black">
                          {course.units} UNITS
                        </div>
                      </div>
                      <div className="text-sm text-gray-800 leading-relaxed">{course.name}</div>
                    </div>
                  );
                })}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Flowchart;
