import React from 'react';
import { motion } from 'framer-motion';
import { Course } from '../types';
import { BookOpen, Clock, Users, Tag } from 'lucide-react';

interface CourseCardProps {
  course: Course;
  onClick: (course: Course) => void;
  isSelected?: boolean;
}

const CourseCard: React.FC<CourseCardProps> = ({ course, onClick, isSelected = false }) => {
  const getCategoryColor = (category: string) => {
    switch (category) {
      case 'Core':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'Technical':
        return 'bg-purple-100 text-purple-800 border-purple-200';
      case 'Elective':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'Complementary':
        return 'bg-orange-100 text-orange-800 border-orange-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getTagColor = (tag: string) => {
    switch (tag) {
      case 'T':
        return 'bg-teal-100 text-teal-800';
      case 'E':
        return 'bg-emerald-100 text-emerald-800';
      case 'M':
        return 'bg-indigo-100 text-indigo-800';
      case 'P':
        return 'bg-rose-100 text-rose-800';
      case 'S':
        return 'bg-amber-100 text-amber-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <motion.div
      whileHover={{ scale: 1.02 }}
      whileTap={{ scale: 0.98 }}
      className={`course-card p-4 ${isSelected ? 'ring-2 ring-primary-500' : ''}`}
      onClick={() => onClick(course)}
    >
      <div className="flex items-start justify-between mb-2">
        <div className="flex-1">
          <h3 className="font-semibold text-sm text-gray-900 mb-1">{course.code}</h3>
          <p className="text-xs text-gray-600 font-medium leading-tight">{course.name}</p>
        </div>
        <div className="flex items-center space-x-1 ml-2">
          <Clock className="w-3 h-3 text-gray-400" />
          <span className="text-xs text-gray-500">{course.units} units</span>
        </div>
      </div>

      <div className="flex items-center justify-between mb-2">
        <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium border ${getCategoryColor(course.category)}`}>
          {course.category}
        </span>
        
        {course.tags && course.tags.length > 0 && (
          <div className="flex space-x-1">
            {course.tags.map((tag, index) => (
              <span
                key={index}
                className={`inline-flex items-center px-1.5 py-0.5 rounded text-xs font-medium ${getTagColor(tag)}`}
              >
                {tag}
              </span>
            ))}
          </div>
        )}
      </div>

      <div className="flex items-center text-xs text-gray-500 space-x-3">
        <div className="flex items-center space-x-1">
          <BookOpen className="w-3 h-3" />
          <span>{course.components.join(', ')}</span>
        </div>
      </div>

      {course.prerequisites.length > 0 && (
        <div className="mt-2 pt-2 border-t border-gray-100">
          <div className="flex items-center space-x-1 text-xs text-gray-500">
            <Tag className="w-3 h-3" />
            <span>Prereq: {course.prerequisites.join(', ')}</span>
          </div>
        </div>
      )}
    </motion.div>
  );
};

export default CourseCard;
